scriptData.script:
console.log(this.actor);

// First check if the actor is Fatigued
let actorFatigueCondition = this.actor.data.effects.filter(effect => effect.label === "Fatigued");
let actorFatigueAmount = actorFatigueCondition[0].flags.wfrp4e.value;

// Then check what level of this talent they have
let actorIndefatigableTalent = this.actor.data.talents.find(talent => talent.name === "Indefatigable");
let actorIndefatigableTalentLevel = actorIndefatigableTalent.data.advances.value;

console.log(`Fatigue: ${actorFatigueAmount}; Talent: ${actorIndefatigableTalent }; Level: ${actorIndefatigableTalentLevel}`)

// Then take the difference, ensuring that if the talent level exceeds fatigue it's simply 0.
if (actorFatigueAmount>actorIndefatigableTalentLevel) {
  args.prefillModifiers.modifier = (actorFatigueAmount-actorIndefatigableTalentLevel)*-10
} else {
  args.prefillModifiers.modifier = 0;
}

