scriptData.script:
// Get levels in Terrible Blows
let actorTerribleBlows = args.attacker.data.talents.find(talent=> talent.name === "Terrible Blows");
let terribleBlowsLevel = actorTerribleBlows.data.advances.value;

// Add a message to remind players
let attackerName = args.attacker.name;
let defenderName = args.actor.name;
let messageContent = `<b>${attackerName}'s</b> Terrible Blows deal <b>${terribleBlowsLevel}</b> to <b>${defenderName}</b>`;

// Now set minimum wound loss from the level of Terrible Blows
if (args.totalWoundLoss <= 0) {
  args.totalWoundLoss += terribleBlowsLevel ;
  ChatMessage.create({content: messageContent});
}

