wfrp4e.script:
let advantage = game.settings.get("wfrp4e", "groupAdvantageValues");
if (advantage.enemies >= 2) {
  advantage.enemies = 0;
  game.wfrp4e.utility.updateGroupAdvantage(advantage);
  let newFlag = this.actor.conditions
    .filter(x => x.statuses.first() != 'grappling' && x.statuses.first() != 'engaged')
    .map(x => { 
      let z = {};
      z.name = x.statuses.first(); 
      z.value = x.conditionValue; 
      z.id = x.id;
      return z
    });
  await this.actor.setFlag("wfrp4e", "deathlessvigor", newFlag);
  let ids = newFlag.map(x => x.id);
  await this.actor.deleteEmbeddedDocuments("ActiveEffect", ids);
  let bonus = newFlag.reduce((a,x) => x.value ? a + x.value : a + 1, 0);
  await this.actor.setFlag("wfrp4e", "deathlessvigorbonus", bonus);
}

scriptData.script:
let advantage = game.settings.get("wfrp4e", "groupAdvantageValues");
if (advantage.enemies >= 2) {
  advantage.enemies = 0;
  game.wfrp4e.utility.updateGroupAdvantage(advantage);
  let newFlag = this.actor.conditions
    .filter(x => x.statuses.first() != 'grappling' && x.statuses.first() != 'engaged')
    .map(x => { 
      let z = {};
      z.label = x.name;
      z.name = x.statuses.first(); 
      z.value = x.conditionValue; 
      z.id = x.id;
      return z
    });
  let text = ''
  for (let c of newFlag) {
    text += c.label; 
    if (c.value) {
      text + ' (' + c.value + ')'
    }
    text += ', ';
  }
  let chatData = game.wfrp4e.utility.chatDataSetup(`Wigor Nieśmiertelnego usunął stany: ${text}`, "text", false);
  ChatMessage.create(chatData);
  await this.actor.setFlag("wfrp4e", "deathlessvigor", newFlag);
  let ids = newFlag.map(x => x.id);
  await this.actor.deleteEmbeddedDocuments("ActiveEffect", ids);
  let bonus = newFlag.reduce((a,x) => x.value ? a + x.value : a + 1, 0);
  await this.actor.setFlag("wfrp4e", "deathlessvigorbonus", bonus);
}

