wfrp4e.script:
await this.actor.unsetFlag("wfrp4e", "deathlessvigorbonus");
let arr = this.actor.flags?.wfrp4e?.deathlessvigor;
if (arr?.length > 0) {
  for (let c of arr) {
    if (c.value) {
      await this.actor.addCondition(c.name, c.value);
    } else {
      await this.actor.addCondition(c.name);
    }
  }
  await this.actor.unsetFlag("wfrp4e", "deathlessvigor");
}

