wfrp4e.script:
let item = args.attackerTest.item;
if (item?.attackType != 'melee' && item?.attackType != 'ranged' && args.attackerTest.type != 'cast') return;
if (args.opposedTest.result.winner == "attacker") {
  setTimeout(async () => {
    let firstTest = await args.attackerTest.actor.setupSkill(game.i18n.localize("NAME.Cool"), { title: "Zepsuta Niewinność - Opanowanie" });
    await firstTest.roll();
    let target = args.defenderTest.actor.getActiveTokens()[0];
    await firstTest.createOpposedMessage(target);
    let secondTest = await args.defenderTest.actor.setupSkill(game.i18n.localize("NAME.Charm"), { title: "Zepsuta Niewinność - Charyzma" });
    await secondTest.roll();
    let msg = secondTest.opposedMessages[0];
    let op = msg.getOppose();
    let opr = op.resultMessage.getOpposedTest();
    if (opr.result.winner == "defender") {
        ChatMessage.create({content: "Nie udało się zadać obrażeń - Talent Zepsuta Niewinność"});
        let oldMsg = args.attackerTest.opposedMessages[0];
        let oldOp = oldMsg.getOppose();
        let oldOpr = oldOp.resultMessage;
        let $c = $(oldOpr.content);
        $c.find(".opposed-card").css('text-decoration','line-through');
        let attackerName = args.attackerTest.actor.name;
        $c.find(".opposed-card").parent().append('<div class="opposed-card"><div><b>' + attackerName + '</b> Przegrał z powodu Zepsutej Niewinności</div></div>');
        oldOpr.update({content: $c[0].outerHTML})
    }
  }, 1000);
}

