scriptData.script:
console.log(` --- PRE --- `)
console.log(args)

let attackerTestType = args.opposeResult.attackerTestResult.characteristic;
let defenderTestType = args.opposeResult.defenderTestResult.characteristic; 

let attackerTestCorrectType = attackerTestType === "s" || attackerTestType === "ws";
let defenderTestCorrectType = defenderTestType === "s" || defenderTestType === "ws";

// Some information for a chat message
let attackerName = args.attackerTest.actor.name;
let defenderName = args.defenderTest.actor.name;
let messageContent = `<b>${attackerName}'s</b> Żelazne Ścięgna zmniejszyły PS <b>${defenderName}</b> o <b>2</b>!`;

if (attackerTestCorrectType && defenderTestCorrectType) {
  args.opposeResult.defenderTestResult.SL -= 2;
  args.opposeResult.differenceSL = args.opposeResult.attackerTestResult.SL - args.opposeResult.defenderTestResult.SL
  ChatMessage.create({ content : messageContent })
}

