wfrp4e.script:
if (this.actor.isOwner) {
    args.actor.setupSkill(game.i18n.localize("NAME.Endurance"), {absolute: {difficulty: "difficult"}}).then(async test => {
        await test.roll();
        if (test.result.outcome == "failure") {
            await args.actor.addCondition("stunned");
        }
    });
    let injury = await fromUuid("Compendium.wfrp4e-core.injuries.4lj1ik958mbgAlaF");
    args.actor.createEmbeddedDocuments("Item", [injury.toObject()]);
}
