wfrp4e.script:
let injury = await fromUuid("Compendium.wfrp4e-core.injuries.3S4OYOZLauXctmev");
args.actor.createEmbeddedDocuments("Item", [injury.toObject()]);

if (this.actor.isOwner) {
    args.actor.setupSkill(game.i18n.localize("NAME.Endurance"), {absolute: {difficulty: "hard"}}).then(async test => {
        await test.roll();
        if (test.result.outcome == "failure") {
            await args.actor.addCondition("unconscious");
        }
     });
}
