let injury = await fromUuid("Compendium.wfrp4e-core.injuries.fBcZhOBn8IpoVqQ1");
injury = injury.toObject()
let teeth = (await new Roll("1d10").roll()).total;
ChatMessage.create({content : `<b>${this.actor.prototypeToken.name}</b> loses ${teeth} teeth`, speaker : {alias: this.effect.label}});
injury.system.location.value = `${teeth} ${injury.system.location.value}`;
args.actor.createEmbeddedDocuments("Item", [injury]);

injury = await fromUuid("Compendium.wfrp4e-core.injuries.rkJA1DlK51QuRlJy");
args.actor.createEmbeddedDocuments("Item", [injury.toObject()]);

injury = await fromUuid("Compendium.wfrp4e-core.injuries.hCadFsTRvLN9faaY");
args.actor.createEmbeddedDocuments("Item", [injury.toObject()]);

if (this.actor.isOwner) {
   args.actor.setupSkill(game.i18n.localize("NAME.Endurance")).then(async test => {
        await test.roll();
        if (test.result.outcome == "failure") {
            await args.actor.addCondition("unconscious");
        }
     });
}
