wfrp4e.script:
if (this.actor.isOwner) {
    args.actor.setupSkill(game.i18n.localize("NAME.Endurance"), { absolute: { difficulty: "average" } }).then(async test => {
      await test.roll();
      if (test.result.outcome == "failure") {
            await args.actor.addCondition("prone");
            let injury = await fromUuid("Compendium.wfrp4e-core.injuries.ZhMADOqoo0y8Q9bx");
            injury = injury.toObject();
            injury.system.location.value = `${Math.clamped(Math.abs(test.result.SL) + 1, 1, 5)} ${injury.system.location.value}`
            args.actor.createEmbeddedDocuments("Item", [injury]);
      }
    });
}
