wfrp4e.script:
// Exit if the item being updated isn't the mask
if (args.context === "delete" || this.item.id !== args.item?.id) return

const effectWearing = {
  _id : "mermedus00000000",
  name: "Wearing Mask of Mermedus",
  img: "modules/wfrp4e-core/icons/equipment/clothing_and_accessories/mask.png",
  transfer: true,
  flags: {
    wfrp4e: {
        "effectApplication": "item",
        "effectTrigger": "addItems",
        "hide" : "false",
        "preventDuplicateEffects" : "true",
        "script": `
        const talents = await Promise.all(["Furious Assault", "Sea Legs"].map(game.wfrp4e.utility.findTalent))
        this.actor.createEmbeddedDocuments("Item", talents.map(i => i.toObject()), {fromEffect : 'mermedus00000000'})
        `
    }
  }
}

const effectSwim = {
  label: "Mask of Mermedus - Swim Bonus",
  img: "modules/wfrp4e-core/icons/equipment/clothing_and_accessories/mask.png",
  transfer: true,
  flags: {
      wfrp4e: {
          "effectApplication": "actor",
          "effectTrigger": "prefillDialog",
          "hide" : "false",
          "preventDuplicateEffects": true,
          "script": `
            if (args.type == "skill" && args.item.name == game.i18n.localize("NAME.Swim"))
                    args.prefillModifiers.modifier += 50
          `
      },
  }
}

// Apply changes when the mask is worn

if (args.item.worn) {  
  this.actor.createEmbeddedDocuments("ActiveEffect", [effectWearing, effectSwim])  
  ChatMessage.create({
    content: `
      ${this.actor.name} dons the <strong>${args.item.name}</strong>. <br>
      They gain +50 to Swim Tests.<br>
      If they wear the mask for more than an hour or benefit from any of its effects, they are exposed to @Corruption[moderate]{Moderate Corruption}. 
      `, 
    whisper: ChatMessage.getWhisperRecipients("GM") 
  }) 
}


// Notify of lingering effects when mask is removed

if (!args.item.worn) {
  ChatMessage.create({
    content: `
      <strong>${args.item.name}</strong> on ${this.actor.name} has been taken off. However, the effects last for [[/br 1d10+4]] days, after which they should be manually removed.`, 
    whisper: ChatMessage.getWhisperRecipients("GM") 
  })
}
