wfrp4e.script:
fromUuid(this.effect.origin).then(caster => {
   if (caster)
   {
       let healed= caster.characteristics.wp.bonus + caster.characteristics.int.bonus
       let wounds = duplicate(args.actor.status.wounds)
       wounds.value+=healed

       if (wounds.value > wounds.max)
          wounds.value = wounds.max

       args.actor.update({"system.status.wounds" : wounds})
       ChatMessage.create({content: `${this.actor.prototypeToken.name} regains ${healed} Wounds`, speaker : {alias : this.effect.name}})
   }
})

this.actor.setupSkill(game.i18n.localize("NAME.Endurance"), {absolute : {difficulty : "vhard"}, context : {success : "1 Corruption point that was gained within the last hour is removed.", failure: "Nothing happens"}}).then(setupData => {
   this.actor.basicTest(setupData)
})
