wfrp4e.script:
if (args.test.item?.id == this.item.id) {
  if (args.test.result.critical && args.test.result.roll % 10 == 0) {
    game.wfrp4e.tables.findTable("knuckleduster-diseases").roll().then(roll => {
      let results = roll.results[0]

      ChatMessage.create({ content: `<strong>${this.actor.name}</strong> contracts @UUID[Compendium.${results.documentCollection}.${results.documentId}]{${results.text}}`, speaker: { alias: this.effect.name }, blind : true, whisper: ChatMessage.getWhisperRecipients("GM") })
    })

  }

}
