wfrp4e.script:
this.actor.applyBasicDamage(12, {damageType: game.wfrp4e.config.DAMAGE_TYPE.IGNORE_AP})
this.actor.setupSkill(game.i18n.localize("NAME.Endurance"), {absolute: {difficulty: "hard"}}).then(async test => {
    await test.roll();
    if (test.result.outcome == "failure")
       this.actor.addSystemEffect("cold1")
})
