scriptData.script:
if (args.type == "characteristic") {
  if (args.item == "wp" || args.item == "t")
    args.prefillModifiers.modifier += 10
  else if (args.item == "ag" || args.item == "i" || args.item == "int")
    args.prefillModifiers.modifier -= 10
}
else if (args.type == "skill") {
  if (args.item.characteristic.key == "wp" || args.item.characteristic.key == "t")
  args.prefillModifiers.modifier += 10
  else if (args.item.characteristic.key == "ag" || args.item.characteristic.key == "i" || args.item.characteristic.key == "int")
    args.prefillModifiers.modifier -= 10
}
else if (args.type == "cast") {
  args.prefillModifiers.modifier -= 10
}
else if (args.type == "channelling") {
  args.prefillModifiers.modifier += 10
}

